import React, { useState } from 'react';

export const WizardForm: React.FC = () => {
  const [step, setStep] = useState(1);
  const totalSteps = 3;

  const handleNext = () => setStep((prev) => Math.min(prev + 1, totalSteps));
  const handleBack = () => setStep((prev) => Math.max(prev - 1, 1));

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-xl shadow-lg border border-gray-100 dark:bg-gray-800 dark:border-gray-700">
      {/* Progress Bar */}
      <div className="mb-6">
        <div className="flex justify-between mb-2 text-sm font-medium text-gray-500">
          <span>Step {step} of {totalSteps}</span>
          <span>{Math.round((step / totalSteps) * 100)}%</span>
        </div>
        <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
          <div 
            className="h-full bg-blue-600 transition-all duration-300 ease-out"
            style={{ width: `${(step / totalSteps) * 100}%` }}
          />
        </div>
      </div>

      {/* Content */}
      <div className="min-h-[150px] mb-6">
        {step === 1 && (
          <div>
            <h2 className="text-xl font-bold mb-4">Personal Info</h2>
            <input type="text" placeholder="Full Name" className="w-full p-2 border rounded mb-2" />
          </div>
        )}
        {step === 2 && (
          <div>
            <h2 className="text-xl font-bold mb-4">Account Details</h2>
            <input type="email" placeholder="Email Address" className="w-full p-2 border rounded mb-2" />
          </div>
        )}
        {step === 3 && (
          <div>
            <h2 className="text-xl font-bold mb-4">Review</h2>
            <p className="text-gray-600">Please review your information before submitting.</p>
          </div>
        )}
      </div>

      {/* Navigation */}
      <div className="flex justify-between">
        <button
          onClick={handleBack}
          disabled={step === 1}
          className="px-4 py-2 text-gray-600 bg-gray-100 rounded-lg disabled:opacity-50 hover:bg-gray-200"
        >
          Back
        </button>
        <button
          onClick={handleNext}
          className="px-4 py-2 text-white bg-blue-600 rounded-lg hover:bg-blue-700"
        >
          {step === totalSteps ? 'Submit' : 'Next'}
        </button>
      </div>
    </div>
  );
};

