import React, { useState, useEffect } from 'react';

const items = ['Apple', 'Banana', 'Cherry', 'Date', 'Elderberry', 'Fig', 'Grape', 'Honeydew'];

const DebouncedSearch: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [debouncedTerm, setDebouncedTerm] = useState<string>('');
  const [results, setResults] = useState<string[]>(items);

  // Debounce Logic
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedTerm(searchTerm);
    }, 300);

    return () => {
      clearTimeout(handler);
    };
  }, [searchTerm]);

  // Filter Logic
  useEffect(() => {
    if (debouncedTerm) {
      setResults(items.filter(item => item.toLowerCase().includes(debouncedTerm.toLowerCase())));
    } else {
      setResults(items);
    }
  }, [debouncedTerm]);

  return (
    <div className="w-full max-w-sm mx-auto p-4">
      <div className="relative">
        <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
          <svg className="w-5 h-5 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path></svg>
        </div>
        <input
          type="text"
          className="block w-full p-3 pl-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 outline-none transition-shadow"
          placeholder="Search fruits..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <ul className="mt-4 space-y-2">
        {results.length > 0 ? (
          results.map((item, index) => (
            <li key={index} className="p-3 bg-white rounded-md shadow-sm border border-gray-100 text-gray-700 animate-fade-in">
              {item}
            </li>
          ))
        ) : (
          <li className="text-center text-gray-500 text-sm mt-4">No results found.</li>
        )}
      </ul>
    </div>
  );
};

export default DebouncedSearch;

