import { useState, useEffect } from 'react';
import { Loader2, Search } from 'lucide-react';

const DebouncedSearch = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (query) {
        setIsLoading(true);
        // Simulate API call
        setTimeout(() => {
          setResults([
            `Result for ${query} 1`,
            `Result for ${query} 2`,
            `Result for ${query} 3`
          ]);
          setIsLoading(false);
        }, 1000);
      } else {
        setResults([]);
        setIsLoading(false);
      }
    }, 500); // 500ms debounce

    return () => clearTimeout(timer);
  }, [query]);

  return (
    <div className="w-full max-w-md mx-auto mt-10">
      <div className="relative">
        <input
          type="text"
          className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent outline-none transition-all"
          placeholder="Search database..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <Search className="absolute left-3 top-2.5 text-gray-400" size={20} />
        {isLoading && (
          <Loader2 className="absolute right-3 top-2.5 text-indigo-600 animate-spin" size={20} />
        )}
      </div>

      {results.length > 0 && !isLoading && (
        <ul className="mt-2 bg-white border border-gray-200 rounded-lg shadow-lg overflow-hidden">
          {results.map((res, idx) => (
            <li key={idx} className="px-4 py-2 hover:bg-gray-50 cursor-pointer text-gray-700">
              {res}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default DebouncedSearch;

import { useState } from 'react';
import { X, CheckCircle } from 'lucide-react';

interface Toast {
  id: number;
  message: string;
}

const ToastSystem = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = () => {
    const id = Date.now();
    setToasts([...toasts, { id, message: 'Action completed successfully!' }]);

    // Auto remove after 3 seconds
    setTimeout(() => {
      setToasts((current) => current.filter((t) => t.id !== id));
    }, 3000);
  };

  const removeToast = (id: number) => {
    setToasts((current) => current.filter((t) => t.id !== id));
  };

  return (
    <div className="p-10 flex flex-col items-center">
      <button
        onClick={addToast}
        className="px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors shadow-md"
      >
        Trigger Toast
      </button>

      {/* Toast Container */}
      <div className="fixed bottom-5 right-5 flex flex-col gap-3 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className="flex items-center p-4 bg-white border-l-4 border-green-500 rounded shadow-lg animate-slide-in-right min-w-[300px]"
          >
            <CheckCircle className="text-green-500 mr-3" size={20} />
            <span className="text-gray-800 font-medium flex-1">{toast.message}</span>
            <button
              onClick={() => removeToast(toast.id)}
              className="ml-4 text-gray-400 hover:text-gray-600"
            >
              <X size={18} />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastSystem;

