          </div>
        )}
        {step === 2 && (
          <div className="animate-fade-in">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Account Details</h2>
            <input type="text" placeholder="Username" className="w-full p-3 border rounded-lg mb-3" />
