import React, { useState } from 'react';

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({ name: '', email: '', plan: '' });

  const handleNext = () => {
    if (step === 1 && (!formData.name || !formData.email)) return alert('Please fill in all fields');
    if (step === 2 && !formData.plan) return alert('Please select a plan');
    if (step < 3) setStep(step + 1);
  };

  const handleBack = () => step > 1 && setStep(step - 1);

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded-xl shadow-lg">
      {/* Progress Bar */}
      <div className="flex items-center justify-between mb-8">
        {[1, 2, 3].map((num) => (
          <div key={num} className="flex items-center">
            <div className={`w-8 h-8 flex items-center justify-center rounded-full font-bold ${step >= num ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-500'}`}>
              {num}
            </div>
            {num < 3 && <div className={`w-16 h-1 mx-2 ${step > num ? 'bg-indigo-600' : 'bg-gray-200'}`} />}
          </div>
        ))}
      </div>

      {/* Step Content */}
      <div className="min-h-[200px]">
        {step === 1 && (
          <div className="space-y-4">
            <h2 className="text-xl font-bold">Personal Info</h2>
            <input 
              type="text" placeholder="Full Name" 
              className="w-full p-2 border rounded focus:ring-2 focus:ring-indigo-500"
              value={formData.name} onChange={e => setFormData({...formData, name: e.target.value})}
            />
            <input 
              type="email" placeholder="Email Address" 
              className="w-full p-2 border rounded focus:ring-2 focus:ring-indigo-500"
              value={formData.email} onChange={e => setFormData({...formData, email: e.target.value})}
            />
          </div>
        )}
        {step === 2 && (
          <div className="space-y-4">
            <h2 className="text-xl font-bold">Select Plan</h2>
            <select 
              className="w-full p-2 border rounded focus:ring-2 focus:ring-indigo-500"
              value={formData.plan} onChange={e => setFormData({...formData, plan: e.target.value})}
            >
              <option value="">Select a plan...</option>
              <option value="free">Free</option>
              <option value="pro">Pro ($10/mo)</option>
            </select>
          </div>
        )}
        {step === 3 && (
          <div className="text-center">
            <h2 className="text-xl font-bold text-green-600">Success!</h2>
            <p className="text-gray-600 mt-2">You have successfully registered.</p>
            <pre className="mt-4 bg-gray-100 p-2 rounded text-left text-xs">{JSON.stringify(formData, null, 2)}</pre>
          </div>
        )}
      </div>

      {/* Navigation */}
      <div className="flex justify-between mt-8">
        <button 
          onClick={handleBack} disabled={step === 1}
          className="px-4 py-2 text-gray-600 bg-gray-100 rounded hover:bg-gray-200 disabled:opacity-50"
        >
          Back
        </button>
        {step < 3 ? (
           <button 
             onClick={handleNext}
             className="px-4 py-2 text-white bg-indigo-600 rounded hover:bg-indigo-700"
           >
             Next
           </button>
        ) : (
          <button className="px-4 py-2 text-white bg-green-600 rounded hover:bg-green-700">Finish</button>
        )}
      </div>
    </div>
  );
};

export default MultiStepForm;

import React, { useState, DragEvent } from 'react';

const FileUpload = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [files, setFiles] = useState<File[]>([]);

  const handleDragOver = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      setFiles((prev) => [...prev, ...Array.from(e.dataTransfer.files)]);
    }
  };

  return (
    <div className="p-6 max-w-lg mx-auto">
      <div
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
        className={`
          relative border-2 border-dashed rounded-xl p-10 flex flex-col items-center justify-center text-center transition-all duration-200
          ${isDragging ? 'border-blue-500 bg-blue-50' : 'border-gray-300 bg-gray-50'}
        `}
      >
        <div className="text-4xl mb-3">☁️</div>
        <p className="text-gray-700 font-medium">Drag & drop files here</p>
        <p className="text-gray-400 text-sm mt-1">or click to browse</p>
        <input 
          type="file" 
          multiple 
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer" 
          onChange={(e) => e.target.files && setFiles(prev => [...prev, ...Array.from(e.target.files!)])}
        />
      </div>

      {files.length > 0 && (
        <div className="mt-6">
          <h4 className="text-sm font-semibold text-gray-500 mb-2">Uploaded Files:</h4>
          <ul className="space-y-2">
            {files.map((file, idx) => (
              <li key={idx} className="flex items-center justify-between p-3 bg-white border border-gray-200 rounded-lg shadow-sm">
                <span className="text-sm text-gray-800 truncate max-w-[200px]">{file.name}</span>
                <span className="text-xs text-gray-400">{(file.size / 1024).toFixed(1)} KB</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default FileUpload;

