import React, { useState } from 'react';

interface Toast {
  id: number;
  message: string;
  type: 'success' | 'error' | 'info';
}

const ToastNotificationSystem = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = (message: string, type: Toast['type']) => {
    const id = Date.now();
    setToasts((prev) => [...prev, { id, message, type }]);

    // Auto remove after 3 seconds
    setTimeout(() => {
      setToasts((prev) => prev.filter((toast) => toast.id !== id));
    }, 3000);
  };

  const removeToast = (id: number) => {
    setToasts((prev) => prev.filter((toast) => toast.id !== id));
  };

  const getStyles = (type: Toast['type']) => {
    switch (type) {
      case 'success': return 'bg-green-100 text-green-800 border-green-200';
      case 'error': return 'bg-red-100 text-red-800 border-red-200';
      default: return 'bg-blue-100 text-blue-800 border-blue-200';
    }
  };

  return (
    <div className="p-4">
      <div className="flex gap-2">
        <button onClick={() => addToast('Changes saved!', 'success')} className="px-4 py-2 bg-green-500 text-white rounded">Success</button>
        <button onClick={() => addToast('Connection failed.', 'error')} className="px-4 py-2 bg-red-500 text-white rounded">Error</button>
        <button onClick={() => addToast('New update available.', 'info')} className="px-4 py-2 bg-blue-500 text-white rounded">Info</button>
      </div>

      {/* Toast Container */}
      <div className="fixed bottom-5 right-5 z-50 flex flex-col gap-3">
        {toasts.map((toast) => (
          <div 
            key={toast.id} 
            className={`min-w-[250px] px-4 py-3 rounded-lg shadow-lg border flex justify-between items-center animate-slideInLeft transition-all ${getStyles(toast.type)}`}
          >
            <span className="text-sm font-medium">{toast.message}</span>
            <button onClick={() => removeToast(toast.id)} className="ml-4 text-lg font-bold opacity-50 hover:opacity-100">×</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ToastNotificationSystem;

import React, { useState, useEffect, useRef, useCallback } from 'react';

const InfiniteScroll = () => {
  const [items, setItems] = useState<number[]>(Array.from({ length: 20 }, (_, i) => i));
  const [isLoading, setIsLoading] = useState(false);
  const observerRef = useRef<IntersectionObserver | null>(null);
  const loadMoreRef = useRef<HTMLDivElement | null>(null);

  // Simulate API fetch
  const fetchMoreItems = async () => {
    setIsLoading(true);
    await new Promise((resolve) => setTimeout(resolve, 1000));
    setItems((prev) => [
      ...prev,
      ...Array.from({ length: 10 }, (_, i) => prev.length + i)
    ]);
    setIsLoading(false);
  };

  const handleObserver = useCallback((entries: IntersectionObserverEntry[]) => {
    const [target] = entries;
    if (target.isIntersecting && !isLoading) {
      fetchMoreItems();
    }
  }, [isLoading]);

  useEffect(() => {
    const option = { root: null, rootMargin: '20px', threshold: 0 };
    observerRef.current = new IntersectionObserver(handleObserver, option);

    if (loadMoreRef.current) observerRef.current.observe(loadMoreRef.current);

    return () => {
      if (observerRef.current) observerRef.current.disconnect();
    };
  }, [handleObserver]);

  return (
    <div className="max-w-md mx-auto p-4 h-[400px] overflow-y-auto border rounded-xl shadow-inner bg-gray-50">
      <h2 className="text-xl font-bold mb-4 sticky top-0 bg-gray-50 pb-2">Infinite List</h2>
      <ul className="space-y-2">
        {items.map((item) => (
          <li key={item} className="p-4 bg-white rounded shadow text-center font-mono text-gray-700">
            Item #{item + 1}
          </li>
        ))}
      </ul>

      {/* Trigger Element */}
      <div ref={loadMoreRef} className="h-10 flex items-center justify-center mt-4">
        {isLoading && (
          <div className="flex items-center space-x-2 text-gray-500">
             <div className="w-4 h-4 border-2 border-gray-500 border-t-transparent rounded-full animate-spin"></div>
             <span>Loading more...</span>
          </div>
        )}
      </div>
    </div>
  );
};

export default InfiniteScroll;
